// client/src/api.js
const API_BASE = "http://localhost:5000";

// AI Room Summary
export const getRoomSummary = async (data) => {
  const response = await fetch(`${API_BASE}/api/room-summary`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(data),
  });
  return response.json();
};

// Psychrometric Table (AI)
export const getPsychTable = async (data) => {
  const response = await fetch(`${API_BASE}/api/psych-table`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(data),
  });
  return response.json();
};

// Final Insurance-Level Summary
export const getFinalSummary = async (data) => {
  const response = await fetch(`${API_BASE}/api/final-summary`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(data),
  });
  return response.json();
};
