import React, { useState } from "react";
import jsPDF from "jspdf";
import "../styles.css";

const MitigationForm = () => {
  const [formData, setFormData] = useState({
    customerName: "",
    jobAddress: "",
    moistureTable: [{ room: "", reading: "" }],
    notes: "",
    paymentStatus: "Pending",
    beforePhoto: null,
    afterPhoto: null,
    clientSignature: "",
  });

  // Handle inputs & table
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleTableChange = (index, field, value) => {
    const updatedTable = [...formData.moistureTable];
    updatedTable[index][field] = value;
    setFormData({ ...formData, moistureTable: updatedTable });
  };

  const addRow = () => {
    setFormData({
      ...formData,
      moistureTable: [...formData.moistureTable, { room: "", reading: "" }],
    });
  };

  // Handle photo uploads
  const handleFileUpload = (e, type) => {
    setFormData({ ...formData, [type]: e.target.files[0] });
  };

  // PDF FUNCTION
  const generatePDF = () => {
    const doc = new jsPDF();
    doc.text("ToolbeltAI | Mitigation Job Report", 10, 10);
    doc.text(`Customer: ${formData.customerName}`, 10, 20);
    doc.text(`Address: ${formData.jobAddress}`, 10, 30);

    // Moisture Table
    doc.text("Moisture Readings:", 10, 40);
    formData.moistureTable.forEach((row, i) => {
      doc.text(`${row.room}: ${row.reading}`, 10, 50 + i * 10);
    });

    doc.text(`Notes: ${formData.notes}`, 10, 90);
    doc.text(`Payment Status: ${formData.paymentStatus}`, 10, 100);
    doc.text(`Client Signature: ${formData.clientSignature}`, 10, 110);

    doc.save("mitigation_report.pdf");
  };

  // Export to ServiceTitan format
  const exportServiceTitan = () => {
    const exportData = {
      customer: formData.customerName,
      address: formData.jobAddress,
      moistureReadings: formData.moistureTable,
      notes: formData.notes,
      paymentStatus: formData.paymentStatus,
    };
    console.log("ServiceTitan JSON Export:", exportData);
    alert("Data exported to console (ServiceTitan Format)!");
  };

  return (
    <div className="mitigation-form-container">
      <div className="card form-section">
        <h2>ToolbeltAI Mitigation Logger</h2>

        <input name="customerName" placeholder="Customer Name" onChange={handleChange} />
        <input name="jobAddress" placeholder="Job Address" onChange={handleChange} />

        <h3>Moisture Table</h3>
        {formData.moistureTable.map((row, index) => (
          <div key={index} className="table-row">
            <input
              placeholder="Room (e.g. Living Room)"
              value={row.room}
              onChange={(e) => handleTableChange(index, "room", e.target.value)}
            />
            <input
              placeholder="Reading (e.g. 22%)"
              value={row.reading}
              onChange={(e) => handleTableChange(index, "reading", e.target.value)}
            />
          </div>
        ))}
        <button className="add-row-btn" onClick={addRow}>âž• Add Row</button>

        <textarea name="notes" placeholder="Job Notes" onChange={handleChange} />

        <input
          type="text"
          name="clientSignature"
          placeholder="Client Signature / Initials"
          onChange={handleChange}
        />

        <h4>Upload Photos</h4>
        <input type="file" accept="image/*" onChange={(e) => handleFileUpload(e, "beforePhoto")} />
        <input type="file" accept="image/*" onChange={(e) => handleFileUpload(e, "afterPhoto")} />

        <select name="paymentStatus" onChange={handleChange}>
          <option value="Pending">Pending</option>
          <option value="Paid">Paid</option>
          <option value="Waiting on Insurance">Waiting on Insurance</option>
        </select>

        <button className="pdf-btn" onClick={generatePDF}>ðŸ“„ Download PDF</button>
        <button className="service-btn" onClick={exportServiceTitan}>ðŸ“¤ Export to ServiceTitan</button>
      </div>

      {/* RIGHT SIDE PANEL */}
      <div className="card notes-section">
        <h3>Job Summary</h3>
        <p><strong>Customer:</strong> {formData.customerName || "â€”"}</p>
        <p><strong>Address:</strong> {formData.jobAddress || "â€”"}</p>

        <strong>Moisture Readings:</strong>
        {formData.moistureTable.map((row, i) => (
          <p key={i}>{row.room || "â€”"}: {row.reading || "â€”"}</p>
        ))}

        <p><strong>Notes:</strong> {formData.notes || "â€”"}</p>
        <p><strong>Signature:</strong> {formData.clientSignature || "â€”"}</p>

        <span className={`status-badge ${
          formData.paymentStatus === "Paid" ? "status-paid" :
          formData.paymentStatus === "Waiting on Insurance" ? "status-insurance" : "status-pending"
        }`}>
          {formData.paymentStatus}
        </span>
      </div>
    </div>
  );
};

export default MitigationForm;
