import React, { useState } from "react";
import jsPDF from "jspdf";
import "../styles.css"; // important: correct path to styles

const MitigationForm = () => {
  const [formData, setFormData] = useState({
    jobAddress: "",
    customerName: "",
    moistureLevels: "",
    notes: "",
    paymentStatus: "Pending",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // PDF EXPORT FUNCTION
  const generatePDF = () => {
    const doc = new jsPDF();
    doc.text("ToolbeltAI | Mitigation Job Report", 10, 10);
    doc.text(`Customer: ${formData.customerName}`, 10, 20);
    doc.text(`Address: ${formData.jobAddress}`, 10, 30);
    doc.text(`Moisture Levels: ${formData.moistureLevels}`, 10, 40);
    doc.text(`Notes: ${formData.notes}`, 10, 50);
    doc.text(`Payment Status: ${formData.paymentStatus}`, 10, 60);
    doc.save("mitigation_report.pdf"); // name of exported file
  };

  return (
    <div className="mitigation-form-container">

      {/* LEFT SIDE â€” FORM */}
      <div className="card form-section">
        <h2>ðŸ›  Mitigation Job Report</h2>

        <input
          type="text"
          name="customerName"
          placeholder="Customer Name"
          onChange={handleChange}
        />

        <input
          type="text"
          name="jobAddress"
          placeholder="Job Address"
          onChange={handleChange}
        />

        <textarea
          name="moistureLevels"
          placeholder="Moisture Readings / Equipment Used"
          onChange={handleChange}
        />

        <textarea
          name="notes"
          placeholder="General Notes / What Was Done On Site"
          onChange={handleChange}
        />

        <select name="paymentStatus" onChange={handleChange}>
          <option value="Pending">Pending</option>
          <option value="Paid">Paid</option>
          <option value="Waiting on Insurance">Waiting on Insurance</option>
        </select>

        <button className="pdf-btn" onClick={generatePDF}>
          ðŸ“„ Download Insurance Report (PDF)
        </button>
      </div>

      {/* RIGHT SIDE â€” SIDE PANEL */}
      <div className="card notes-section">
        <h3>ðŸ“Œ Job Summary</h3>
        <p><strong>Customer:</strong> {formData.customerName || "â€”"}</p>
        <p><strong>Address:</strong> {formData.jobAddress || "â€”"}</p>
        <p><strong>Moisture:</strong> {formData.moistureLevels || "â€”"}</p>
        <p><strong>Notes:</strong> {formData.notes || "â€”"}</p>

        <strong>Payment Status:</strong>
        <span
          className={`status-badge ${
            formData.paymentStatus === "Paid"
              ? "status-paid"
              : formData.paymentStatus === "Waiting on Insurance"
              ? "status-insurance"
              : "status-pending"
          }`}
        >
          {formData.paymentStatus}
        </span>
      </div>

    </div>
  );
};

export default MitigationForm;
