// src/components/MitigationForm.js
import React, { useState } from "react";
import api from "../api";
import jsPDF from "jspdf";

const initialJobInfo = {
  customerName: "",
  jobAddress: "",
  claimNumber: "",
  jobType: "",
  paymentStatus: "",
  waitingOnInsurance: "",
  notes: "",
};

const jobTypes = [
  "Water Mitigation",
  "Fire / Smoke",
  "Mold Remediation",
  "Sewage / Category 3",
  "Storm / Roof Leak",
  "Other",
];

const paymentStatuses = [
  "Customer Paid",
  "Waiting on Insurance",
  "Deductible Only Collected",
  "Third Party (Property Mgmt / Builder)",
];

const waitingOptions = ["Yes", "No", "Partial"];

function MitigationForm() {
  const [jobInfo, setJobInfo] = useState(initialJobInfo);
  const [rooms, setRooms] = useState([
    {
      id: Date.now(),
      name: "",
      moistureReadings: "",
      photos: [],
      aiSummary: "",
    },
  ]);
  const [psychrometricTable, setPsychrometricTable] = useState("");
  const [finalSummary, setFinalSummary] = useState("");
  const [savingMessage, setSavingMessage] = useState("");
  const [loading, setLoading] = useState({
    room: false,
    psych: false,
    final: false,
    saving: false,
    pdf: false,
  });

  const handleJobChange = (field, value) => {
    setJobInfo((prev) => ({ ...prev, [field]: value }));
  };

  const handleRoomChange = (index, field, value) => {
    setRooms((prev) =>
      prev.map((room, i) =>
        i === index
          ? {
              ...room,
              [field]: value,
            }
          : room
      )
    );
  };

  const handleRoomPhotos = (index, files) => {
    const photos = Array.from(files || []);
    setRooms((prev) =>
      prev.map((room, i) =>
        i === index
          ? {
              ...room,
              photos,
            }
          : room
      )
    );
  };

  const addRoom = () => {
    setRooms((prev) => [
      ...prev,
      {
        id: Date.now() + Math.random(),
        name: "",
        moistureReadings: "",
        photos: [],
        aiSummary: "",
      },
    ]);
  };

  const handleGenerateRoomSummary = async (index) => {
    const room = rooms[index];
    if (!room.name || !room.moistureReadings) {
      alert("Please fill in room name and moisture readings first.");
      return;
    }

    try {
      setLoading((prev) => ({ ...prev, room: true }));
      const response = await api.post("/room-summary", {
        roomName: room.name,
        moistureReadings: room.moistureReadings,
        jobType: jobInfo.jobType,
        claimNumber: jobInfo.claimNumber,
        jobAddress: jobInfo.jobAddress,
        photosCount: room.photos.length,
      });

      const summary = response.data.summary || "";
      setRooms((prev) =>
        prev.map((r, i) => (i === index ? { ...r, aiSummary: summary } : r))
      );
    } catch (err) {
      console.error(err);
      alert("AI Error on room summary ‚Äì check backend console.");
    } finally {
      setLoading((prev) => ({ ...prev, room: false }));
    }
  };

  const handleGeneratePsychTable = async () => {
    if (!rooms.some((r) => r.moistureReadings)) {
      alert("Add at least one room with moisture readings first.");
      return;
    }

    try {
      setLoading((prev) => ({ ...prev, psych: true }));
      const response = await api.post("/psych-table", {
        jobInfo,
        rooms: rooms.map((r) => ({
          name: r.name,
          moistureReadings: r.moistureReadings,
        })),
      });

      setPsychrometricTable(response.data.table || "");
    } catch (err) {
      console.error(err);
      alert("AI Error on psychrometric table ‚Äì check backend console.");
    } finally {
      setLoading((prev) => ({ ...prev, psych: false }));
    }
  };

  const handleGenerateFinalSummary = async () => {
    if (!psychrometricTable) {
      const proceed = window.confirm(
        "You have not generated a Psychrometric Table yet. Generate final summary anyway?"
      );
      if (!proceed) return;
    }

    try {
      setLoading((prev) => ({ ...prev, final: true }));
      const response = await api.post("/final-summary", {
        jobInfo: {
          customerName: jobInfo.customerName,
          jobAddress: jobInfo.jobAddress,
          claimNumber: jobInfo.claimNumber,
          jobType: jobInfo.jobType,
        },
        rooms: rooms.map((r) => ({
          name: r.name,
          aiSummary: r.aiSummary,
        })),
        psychrometricTable,
        notes: jobInfo.notes,
      });

      setFinalSummary(response.data.summary || "");
    } catch (err) {
      console.error(err);
      alert("AI Error on final insurance summary ‚Äì check backend console.");
    } finally {
      setLoading((prev) => ({ ...prev, final: false }));
    }
  };

  const handleSaveToDatabase = async () => {
    try {
      setLoading((prev) => ({ ...prev, saving: true }));
      setSavingMessage("");
      const response = await api.post("/save-report", {
        jobInfo,
        rooms: rooms.map((r) => ({
          name: r.name,
          moistureReadings: r.moistureReadings,
          photosCount: r.photos.length,
          aiSummary: r.aiSummary,
        })),
        psychrometricTable,
        finalSummary,
      });

      setSavingMessage(
        response.data?.success
          ? "Report saved to database."
          : "Saved response received."
      );
    } catch (err) {
      console.error(err);
      setSavingMessage("Error saving report ‚Äì check backend console.");
    } finally {
      setLoading((prev) => ({ ...prev, saving: false }));
      setTimeout(() => setSavingMessage(""), 4000);
    }
  };

  const handleExportPdf = () => {
    try {
      setLoading((prev) => ({ ...prev, pdf: true }));
      const doc = new jsPDF();
      const marginX = 14;
      let y = 18;

      const addTitle = (text) => {
        doc.setFontSize(16);
        doc.setFont("helvetica", "bold");
        doc.text(text, marginX, y);
        y += 8;
      };

      const addSectionTitle = (text) => {
        if (y > 270) {
          doc.addPage();
          y = 18;
        }
        doc.setFontSize(12);
        doc.setFont("helvetica", "bold");
        doc.text(text, marginX, y);
        y += 6;
      };

      const addTextBlock = (label, value) => {
        if (!value) return;
        if (y > 270) {
          doc.addPage();
          y = 18;
        }
        doc.setFontSize(10);
        doc.setFont("helvetica", "bold");
        if (label) {
          doc.text(`${label}:`, marginX, y);
        }
        doc.setFont("helvetica", "normal");
        const textLines = doc.splitTextToSize(String(value), 180);
        const textY = label ? y : y;
        const textX = label ? marginX + 24 : marginX;
        doc.text(textLines, textX, textY);
        y += 4 + textLines.length * 4;
      };

      addTitle("ToolbeltAI Mitigation Report");

      addSectionTitle("Job Information");
      addTextBlock("Customer", jobInfo.customerName);
      addTextBlock("Address", jobInfo.jobAddress);
      addTextBlock("Claim #", jobInfo.claimNumber);
      addTextBlock("Job Type", jobInfo.jobType);

      addSectionTitle("Rooms & Readings");
      rooms.forEach((room, index) => {
        addTextBlock(
          `Room ${index + 1}`,
          `${room.name || "N/A"} | Moisture: ${
            room.moistureReadings || "N/A"
          } | Photos: ${
            room.photos.length ? `${room.photos.length} photos on file` : "None"
          }`
        );
        if (room.aiSummary) {
          addTextBlock("Room Narrative", room.aiSummary);
        }
      });

      if (psychrometricTable) {
        addSectionTitle("Psychrometric Table (AI)");
        addTextBlock("", psychrometricTable);
      }

      if (finalSummary) {
        addSectionTitle("Final Insurance-Level Summary");
        addTextBlock("", finalSummary);
      }

      if (jobInfo.notes) {
        addSectionTitle("Technician Notes (Internal)");
        addTextBlock("", jobInfo.notes);
      }

      doc.save(
        `ToolbeltAI_Mitigation_Report_${jobInfo.claimNumber || "demo"}.pdf`
      );
    } catch (err) {
      console.error(err);
      alert("Error generating PDF.");
    } finally {
      setLoading((prev) => ({ ...prev, pdf: false }));
    }
  };

  return (
    <div className="miti-wrapper">
      <header className="miti-header">
        <div className="logo-wrap">
          <img
            src="/toolbeltai-logo.png"
            alt="ToolbeltAI Logo"
            className="logo-img"
          />
          <div>
            <div className="logo-title">ToolbeltAI Mitigation Logger</div>
            <div className="logo-subtitle">
              Daily Log, Psychrometrics & Insurance Package (Demo)
            </div>
          </div>
        </div>
      </header>

      <main className="miti-card">
        {/* JOB INFO */}
        <section className="section">
          <h2 className="section-title">üìç Job Information</h2>
          <div className="grid-2">
            <div className="field">
              <label>Customer Name</label>
              <input
                type="text"
                value={jobInfo.customerName}
                onChange={(e) => handleJobChange("customerName", e.target.value)}
              />
            </div>
            <div className="field">
              <label>Claim Number</label>
              <input
                type="text"
                value={jobInfo.claimNumber}
                onChange={(e) => handleJobChange("claimNumber", e.target.value)}
              />
            </div>
          </div>

          <div className="field">
            <label>Job Address</label>
            <input
              type="text"
              value={jobInfo.jobAddress}
              onChange={(e) => handleJobChange("jobAddress", e.target.value)}
            />
          </div>

          <div className="grid-3">
            <div className="field">
              <label>Job Type</label>
              <select
                value={jobInfo.jobType}
                onChange={(e) => handleJobChange("jobType", e.target.value)}
              >
                <option value="">Select Job Type</option>
                {jobTypes.map((jt) => (
                  <option key={jt} value={jt}>
                    {jt}
                  </option>
                ))}
              </select>
            </div>

            <div className="field">
              <label>
                Payment Status{" "}
                <span className="label-note">(internal only)</span>
              </label>
              <select
                value={jobInfo.paymentStatus}
                onChange={(e) =>
                  handleJobChange("paymentStatus", e.target.value)
                }
              >
                <option value="">Select Payment Status</option>
                {paymentStatuses.map((ps) => (
                  <option key={ps} value={ps}>
                    {ps}
                  </option>
                ))}
              </select>
            </div>

            <div className="field">
              <label>
                Waiting on Insurance?{" "}
                <span className="label-note">(internal only)</span>
              </label>
              <select
                value={jobInfo.waitingOnInsurance}
                onChange={(e) =>
                  handleJobChange("waitingOnInsurance", e.target.value)
                }
              >
                <option value="">Select</option>
                {waitingOptions.map((opt) => (
                  <option key={opt} value={opt}>
                    {opt}
                  </option>
                ))}
              </select>
            </div>
          </div>

          <div className="field">
            <label>Technician Notes / Office Notes</label>
            <textarea
              rows={3}
              value={jobInfo.notes}
              onChange={(e) => handleJobChange("notes", e.target.value)}
              placeholder="Internal notes for office only. Not included in AI insurance language."
            />
          </div>
        </section>

        {/* ROOMS */}
        <section className="section">
          <h2 className="section-title">üè† Rooms & Readings</h2>
          {rooms.map((room, index) => (
            <div key={room.id} className="room-card">
              <div className="grid-2">
                <div className="field">
                  <label>Room Name</label>
                  <input
                    type="text"
                    value={room.name}
                    onChange={(e) =>
                      handleRoomChange(index, "name", e.target.value)
                    }
                    placeholder="e.g., Basement, Living Room"
                  />
                </div>
                <div className="field">
                  <label>Moisture Readings</label>
                  <input
                    type="text"
                    value={room.moistureReadings}
                    onChange={(e) =>
                      handleRoomChange(index, "moistureReadings", e.target.value)
                    }
                    placeholder="e.g., 22% bottom plate, 18% drywall"
                  />
                </div>
              </div>

              <div className="field">
                <label>Room Photos (for record)</label>
                <input
                  type="file"
                  multiple
                  accept="image/*"
                  onChange={(e) => handleRoomPhotos(index, e.target.files)}
                />
                {room.photos.length > 0 && (
                  <div className="photo-note">
                    {room.photos.length} photo
                    {room.photos.length > 1 ? "s" : ""} attached (stored locally
                    with this log ‚Äì not sent to AI).
                  </div>
                )}
              </div>

              <button
                type="button"
                className={`btn btn-primary ${
                  loading.room ? "btn-loading" : ""
                }`}
                onClick={() => handleGenerateRoomSummary(index)}
                disabled={loading.room}
              >
                {loading.room
                  ? "Generating Room Narrative with ToolbeltAI..."
                  : "‚öôÔ∏è Generate AI Room Summary"}
              </button>

              {room.aiSummary && (
                <div className="field field-output">
                  <label>Room Narrative (AI)</label>
                  <textarea value={room.aiSummary} readOnly rows={6} />
                </div>
              )}
            </div>
          ))}

          <button
            type="button"
            className="btn btn-secondary"
            onClick={addRoom}
          >
            ‚ûï Add Another Room
          </button>
        </section>

        {/* PSYCH TABLE */}
        <section className="section">
          <h2 className="section-title">üìä Psychrometric Table</h2>

          <button
            type="button"
            className={`btn btn-primary ${
              loading.psych ? "btn-loading" : ""
            }`}
            onClick={handleGeneratePsychTable}
            disabled={loading.psych}
          >
            {loading.psych
              ? "Building Psychrometric Table..."
              : "‚öôÔ∏è Generate Psychrometric Table (AI)"}
          </button>

          {psychrometricTable && (
            <div className="field field-output">
              <label>Psychrometric Overview (AI)</label>
              <textarea value={psychrometricTable} readOnly rows={8} />
            </div>
          )}
        </section>

        {/* FINAL SUMMARY */}
        <section className="section">
          <h2 className="section-title">üìÑ Final Insurance Summary</h2>

          <button
            type="button"
            className={`btn btn-primary ${
              loading.final ? "btn-loading" : ""
            }`}
            onClick={handleGenerateFinalSummary}
            disabled={loading.final}
          >
            {loading.final
              ? "Generating Final Insurance Narrative..."
              : "‚öôÔ∏è Generate Final Summary (AI)"}
          </button>

          {finalSummary && (
            <div className="field field-output">
              <label>Final Carrier-Facing Summary (AI)</label>
              <textarea value={finalSummary} readOnly rows={10} />
            </div>
          )}
        </section>

        {/* FOOTER ACTIONS */}
        <section className="section section-footer">
          <div className="footer-actions">
            <button
              type="button"
              className={`btn btn-secondary ${
                loading.saving ? "btn-loading" : ""
              }`}
              onClick={handleSaveToDatabase}
              disabled={loading.saving}
            >
              {loading.saving ? "Saving to Database..." : "üíæ Save Report"}
            </button>

            <button
              type="button"
              className={`btn btn-accent ${loading.pdf ? "btn-loading" : ""}`}
              onClick={handleExportPdf}
              disabled={loading.pdf}
            >
              {loading.pdf
                ? "Building Insurance-Level PDF..."
                : "üìÅ Export Insurance-Level PDF"}
            </button>
          </div>

          {savingMessage && (
            <div className="save-message">{savingMessage}</div>
          )}

          <p className="disclaimer">
            PDF and AI text are designed for insurance documentation. Payment
            status and collection details are intentionally{" "}
            <strong>not</strong> included in AI narratives or PDF output.
          </p>
        </section>
      </main>
    </div>
  );
}

export default MitigationForm;
