// STEP 1: IMPORTS (NO COMMENT ERRORS)
import React, { useState } from "react";
import jsPDF from "jspdf";
import "../styles.css";  // <— FIXED PATH

const MitigationForm = () => {
  const [formData, setFormData] = useState({
    jobAddress: "",
    customerName: "",
    moistureLevels: "",
    notes: "",
    paymentStatus: "Pending",
  });

  // STEP 2: HANDLE INPUT CHANGE
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // STEP 3: GENERATE PDF
  const generatePDF = () => {
    const doc = new jsPDF();
    doc.text("Mitigation Job Report", 10, 10);
    doc.text(`Customer: ${formData.customerName}`, 10, 20);
    doc.text(`Address: ${formData.jobAddress}`, 10, 30);
    doc.text(`Moisture Levels: ${formData.moistureLevels}`, 10, 40);
    doc.text(`Notes: ${formData.notes}`, 10, 50);
    doc.text(`Payment Status: ${formData.paymentStatus}`, 10, 60);

    doc.save("mitigation_report.pdf");
  };

  return (
    <div className="mitigation-form-container">
      {/* FORM SECTION */}
      <div className="form-section">
        <h2>Mitigation Form</h2>

        <input
          type="text"
          name="customerName"
          placeholder="Customer Name"
          onChange={handleChange}
        />

        <input
          type="text"
          name="jobAddress"
          placeholder="Job Address"
          onChange={handleChange}
        />

        <textarea
          name="moistureLevels"
          placeholder="Moisture Levels"
          onChange={handleChange}
        />

        <textarea
          name="notes"
          placeholder="General Notes / Readings"
          onChange={handleChange}
        />

        <select name="paymentStatus" onChange={handleChange}>
          <option value="Pending">Pending</option>
          <option value="Paid">Paid</option>
          <option value="Waiting on Insurance">Waiting on Insurance</option>
        </select>

        <button className="pdf-btn" onClick={generatePDF}>
          Generate PDF for Insurance
        </button>
      </div>

      {/* NOTES SIDE PANEL */}
      <div className="notes-section">
        <h3>Status Board</h3>
        <p><strong>Payment:</strong> {formData.paymentStatus}</p>
        <p><strong>Notes:</strong> {formData.notes}</p>
      </div>
    </div>
  );
};

export default MitigationForm;
