import React, { useState } from "react";
import axios from "axios";

function MitigationForm() {
  const [customerName, setCustomerName] = useState("");
  const [claimNumber, setClaimNumber] = useState("");
  const [paymentStatus, setPaymentStatus] = useState("Waiting on Insurance");

  const [rooms, setRooms] = useState([
    { roomName: "", moisture: "", details: "", notes: "", photos: [] },
  ]);

  const [dryLogs, setDryLogs] = useState([
    { date: "", room: "", moisture: "" },
  ]);

  const [aiOutput, setAiOutput] = useState("");

  const handleRoomChange = (i, field, value) => {
    const updated = [...rooms];
    updated[i][field] = value;
    setRooms(updated);
  };

  const handlePhotoUpload = (i, files) => {
    const updated = [...rooms];
    updated[i].photos = files;
    setRooms(updated);
  };

  const addRoom = () => {
    setRooms([
      ...rooms,
      { roomName: "", moisture: "", details: "", notes: "", photos: [] },
    ]);
  };

  const handleDryLogChange = (i, field, value) => {
    const updated = [...dryLogs];
    updated[i][field] = value;
    setDryLogs(updated);
  };

  const addDryLog = () => {
    setDryLogs([...dryLogs, { date: "", room: "", moisture: "" }]);
  };

  const generateAiSummary = async () => {
    try {
      const res = await axios.post("http://localhost:5000/api/final-summary", {
        customerName,
        claimNumber,
        paymentStatus,
        rooms,
        dryLogs,
      });

      setAiOutput(res.data.summary);
    } catch (e) {
      alert("AI Error ‚Äì Check backend.");
    }
  };

  return (
    <div className="form-card">
      <h1 className="title">
        üõ†Ô∏è ToolbeltAI ‚Äì Mitigation Report
      </h1>

      {/* CUSTOMER INFO */}
      <section>
        <h2 className="section-title">Customer Info</h2>
        <input
          className="input"
          placeholder="Customer Name"
          value={customerName}
          onChange={(e) => setCustomerName(e.target.value)}
        />
        <input
          className="input"
          placeholder="Claim Number"
          value={claimNumber}
          onChange={(e) => setClaimNumber(e.target.value)}
        />
        <select
          className="input"
          value={paymentStatus}
          onChange={(e) => setPaymentStatus(e.target.value)}
        >
          <option>Waiting on Insurance</option>
          <option>Customer Paid</option>
          <option>Partial Paid</option>
        </select>
      </section>

      {/* ROOMS SECTION */}
      <section>
        <h2 className="section-title">Rooms & Moisture Readings</h2>

        {rooms.map((room, i) => (
          <div key={i} className="room-block">
            <input
              className="input room-input"
              placeholder="Room Name (e.g., Basement)"
              value={room.roomName}
              onChange={(e) =>
                handleRoomChange(i, "roomName", e.target.value)
              }
            />
            <input
              className="input room-input"
              placeholder="Moisture % (ex: 20% walls)"
              value={room.moisture}
              onChange={(e) =>
                handleRoomChange(i, "moisture", e.target.value)
              }
            />
            <input
              className="input room-input"
              placeholder="Moisture Reading Details"
              value={room.details}
              onChange={(e) =>
                handleRoomChange(i, "details", e.target.value)
              }
            />
            <input
              className="input room-input"
              placeholder="Room Notes (what was removed/dried)"
              value={room.notes}
              onChange={(e) =>
                handleRoomChange(i, "notes", e.target.value)
              }
            />

            <label className="upload-label">Upload Photos:</label>
            <input
              type="file"
              multiple
              onChange={(e) => handlePhotoUpload(i, e.target.files)}
            />
          </div>
        ))}

        <button className="add-btn" onClick={addRoom}>
          ‚ûï Add Room
        </button>
      </section>

      {/* DRY LOG SECTION */}
      <section>
        <h2 className="section-title">Daily Dry Log</h2>

        {dryLogs.map((log, i) => (
          <div key={i} className="room-block">
            <input
              className="input room-input"
              type="date"
              value={log.date}
              onChange={(e) =>
                handleDryLogChange(i, "date", e.target.value)
              }
            />
            <input
              className="input room-input"
              placeholder="Room"
              value={log.room}
              onChange={(e) =>
                handleDryLogChange(i, "room", e.target.value)
              }
            />
            <input
              className="input room-input"
              placeholder="Moisture Level (%)"
              value={log.moisture}
              onChange={(e) =>
                handleDryLogChange(i, "moisture", e.target.value)
              }
            />
          </div>
        ))}

        <button className="add-btn" onClick={addDryLog}>
          ‚ûï Add Dry Log
        </button>
      </section>

      {/* FINAL BUTTON */}
      <button className="generate-btn" onClick={generateAiSummary}>
        ‚ö° Generate AI Summary
      </button>

      <textarea
        className="output"
        placeholder="AI summary will appear here..."
        value={aiOutput}
        readOnly
      />
    </div>
  );
}

export default MitigationForm;
