// client/src/api.js
import axios from "axios";

const API = axios.create({
  baseURL: "http://localhost:5000",
});

// AI Room Summary
export const generateRoomSummary = async (roomData) => {
  const response = await API.post("/api/room-summary", roomData);
  return response.data;
};

// AI Psychrometric Table
export const generatePsychrometricTable = async (tableData) => {
  const response = await API.post("/api/psychrometric-table", tableData);
  return response.data;
};

// AI Final Job Summary
export const generateFinalSummary = async (jobData) => {
  const response = await API.post("/api/final-summary", jobData);
  return response.data;
};

export default API;
