import React, { useState } from "react";
import jsPDF from "jspdf";
import "../styles.css"; // Make sure this file exists

const MitigationForm = () => {
  const [formData, setFormData] = useState({
    jobAddress: "",
    jobType: "",
    moistureReadings: "",
    dryingEquipment: "",
    notes: "",
    paymentStatus: "",
    customerPaid: "",
    waitingOnInsurance: "",
    insuranceCompany: "",
    claimNumber: ""
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const generatePDF = () => {
    const doc = new jsPDF();
    doc.setFontSize(16);
    doc.text("ðŸ›  Mitigation Report - ToolbeltAI", 10, 10);

    doc.setFontSize(12);
    Object.entries(formData).forEach(([key, value], index) => {
      doc.text(`${key.replaceAll(/([A-Z])/g, " $1")}: ${value}`, 10, 20 + index * 8);
    });

    doc.save("mitigation_report.pdf");
  };

  return (
    <div className="container">
      <h2 className="title">Mitigation Job Logger</h2>

      <div className="form-grid">
        <input name="jobAddress" placeholder="Job Address" onChange={handleChange} />
        <input name="jobType" placeholder="Type of Job (e.g., Water, Fire)" onChange={handleChange} />
        <textarea name="moistureReadings" placeholder="Moisture Readings" onChange={handleChange}></textarea>
        <textarea name="dryingEquipment" placeholder="Equipment Used" onChange={handleChange}></textarea>
        <textarea name="notes" placeholder="Technician Notes" onChange={handleChange}></textarea>
      </div>

      <h3 className="section-title">ðŸ“Œ Payment & Insurance</h3>
      <div className="form-grid">
        <select name="paymentStatus" onChange={handleChange}>
          <option value="">Select Payment Status</option>
          <option value="paid">Paid</option>
          <option value="pending">Pending</option>
          <option value="not_paid">Not Paid</option>
        </select>

        <select name="customerPaid" onChange={handleChange}>
          <option value="">Did customer pay out of pocket?</option>
          <option value="yes">Yes</option>
          <option value="no">No</option>
        </select>

        <select name="waitingOnInsurance" onChange={handleChange}>
          <option value="">Waiting on Insurance?</option>
          <option value="yes">Yes</option>
          <option value="no">No</option>
        </select>

        <input name="insuranceCompany" placeholder="Insurance Company" onChange={handleChange} />
        <input name="claimNumber" placeholder="Claim Number" onChange={handleChange} />
      </div>

      <button className="btn" onClick={generatePDF}>ðŸ“„ Export PDF for Insurance/ServiceTitan</button>
    </div>
  );
};

export default MitigationForm;
