import React, { useState } from "react";

function MitigationForm() {
  const [jobNumber, setJobNumber] = useState("");
  const [technician, setTechnician] = useState("");
  const [notes, setNotes] = useState("");
  const [equipmentAdded, setEquipmentAdded] = useState([]);
  const [equipmentRemoved, setEquipmentRemoved] = useState([]);
  const [photoNotes, setPhotoNotes] = useState("");
  const [report, setReport] = useState("");

  const [rooms, setRooms] = useState([
    { name: "", moisture: "", tempIn: "", tempOut: "", rhIn: "", rhOut: "" }
  ]);

  const addRoom = () => {
    setRooms([
      ...rooms,
      { name: "", moisture: "", tempIn: "", tempOut: "", rhIn: "", rhOut: "" }
    ]);
  };

  const handleRoomChange = (index, field, value) => {
    const newRooms = [...rooms];
    newRooms[index][field] = value;
    setRooms(newRooms);
  };

  const handleEquipmentAdd = (value) => {
    if (value && value !== "Select Equipment") {
      setEquipmentAdded([...equipmentAdded, value]);
    }
  };

  const handleEquipmentRemove = (value) => {
    if (value && value !== "Select Equipment") {
      setEquipmentRemoved([...equipmentRemoved, value]);
    }
  };

  const generateReport = async () => {
    try {
      const response = await fetch("http://localhost:5000/generate-log", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          jobNumber,
          technician,
          rooms,
          equipmentAdded,
          equipmentRemoved,
          notes,
          photoNotes
        })
      });

      const data = await response.json();
      setReport(data.report);
    } catch (err) {
      console.error("Error generating report", err);
      setReport("Error generating report. Make sure the server is running on port 5000.");
    }
  };

  return (
    <div className="form-container">
      <h2>Job Info</h2>
      <input
        placeholder="Job Number"
        value={jobNumber}
        onChange={(e) => setJobNumber(e.target.value)}
      />
      <input
        placeholder="Technician Name"
        value={technician}
        onChange={(e) => setTechnician(e.target.value)}
      />

      <h2>Rooms</h2>
      {rooms.map((room, index) => (
        <div key={index} className="room-group">
          <input
            placeholder="Room Name (ex: Master Bedroom)"
            value={room.name}
            onChange={(e) => handleRoomChange(index, "name", e.target.value)}
          />
          <textarea
            placeholder="Moisture readings (ex: North wall 16%, East wall 22%)"
            value={room.moisture}
            onChange={(e) => handleRoomChange(index, "moisture", e.target.value)}
          />
          <div className="row">
            <input
              placeholder="Temp IN °F"
              value={room.tempIn}
              onChange={(e) => handleRoomChange(index, "tempIn", e.target.value)}
            />
            <input
              placeholder="Temp OUT °F"
              value={room.tempOut}
              onChange={(e) => handleRoomChange(index, "tempOut", e.target.value)}
            />
          </div>
          <div className="row">
            <input
              placeholder="RH IN %" 
              value={room.rhIn}
              onChange={(e) => handleRoomChange(index, "rhIn", e.target.value)}
            />
            <input
              placeholder="RH OUT %"
              value={room.rhOut}
              onChange={(e) => handleRoomChange(index, "rhOut", e.target.value)}
            />
          </div>
        </div>
      ))}

      <button type="button" onClick={addRoom}>
        ➕ Add Room
      </button>

      <h2>Equipment Added</h2>
      <select onChange={(e) => handleEquipmentAdd(e.target.value)}>
        <option>Select Equipment</option>
        <option>Dehumidifier</option>
        <option>Air Mover</option>
        <option>Air Scrubber</option>
        <option>Injectidry System</option>
        <option>Containment</option>
      </select>
      {equipmentAdded.length > 0 && (
        <p className="chip-list">
          {equipmentAdded.map((item, idx) => (
            <span key={idx} className="chip">{item}</span>
          ))}
        </p>
      )}

      <h2>Equipment Removed</h2>
      <select onChange={(e) => handleEquipmentRemove(e.target.value)}>
        <option>Select Equipment</option>
        <option>Dehumidifier</option>
        <option>Air Mover</option>
        <option>Air Scrubber</option>
        <option>Injectidry System</option>
        <option>Containment</option>
      </select>
      {equipmentRemoved.length > 0 && (
        <p className="chip-list">
          {equipmentRemoved.map((item, idx) => (
            <span key={idx} className="chip removed">{item}</span>
          ))}
        </p>
      )}

      <h2>Photo Notes</h2>
      <textarea
        placeholder="Example: Photo 1 - containment setup in hallway; Photo 2 - dehu and airmovers set in living room."
        value={photoNotes}
        onChange={(e) => setPhotoNotes(e.target.value)}
      />

      <h2>Additional Notes</h2>
      <textarea
        placeholder="Any work performed today… demo, extraction, monitoring, adjustments."
        value={notes}
        onChange={(e) => setNotes(e.target.value)}
      />

      <button type="button" onClick={generateReport}>
        Generate Daily Log
      </button>

      {report && (
        <div className="report-container">
          <h3>Generated Report</h3>
          <pre>{report}</pre>
        </div>
      )}
    </div>
  );
}

export default MitigationForm;
