// client/src/components/MitigationForm.js

import React, { useState } from "react";
import jsPDF from "jspdf"; // <= PDF Library
import "./styles.css";

function MitigationForm() {
  const [jobInfo, setJobInfo] = useState({
    jobNumber: "",
    technicianName: "",
    date: "",
  });

  const [officeInfo, setOfficeInfo] = useState({
    paymentStatus: "",
    officeNotes: "",
  });

  const [aiOutput, setAiOutput] = useState("");

  // AI Log Generation
  const generateLog = () => {
    const log = `
ðŸ§° DAILY MITIGATION LOG
---------------------------
Job #: ${jobInfo.jobNumber}
Technician: ${jobInfo.technicianName}
Date: ${jobInfo.date}

ðŸ¢ PAYMENT / STATUS
---------------------------
Status: ${officeInfo.paymentStatus}
Notes: ${officeInfo.officeNotes}
`;
    setAiOutput(log);
  };

  // PDF Export Function
  const exportToPDF = () => {
    const doc = new jsPDF();
    doc.setFont("helvetica", "bold");
    doc.text("Daily Mitigation Log", 10, 10);

    doc.setFont("helvetica", "normal");
    doc.text(aiOutput, 10, 20);

    doc.save(`mitigation_log_${jobInfo.jobNumber || "job"}.pdf`);
  };

  return (
    <div className="container">

      {/* LEFT SIDE */}
      <div className="form-section">
        <h2>Job Information</h2>
        <input
          type="text"
          placeholder="Job Number"
          value={jobInfo.jobNumber}
          onChange={(e) => setJobInfo({ ...jobInfo, jobNumber: e.target.value })}
        />
        <input
          type="text"
          placeholder="Technician Name"
          value={jobInfo.technicianName}
          onChange={(e) => setJobInfo({ ...jobInfo, technicianName: e.target.value })}
        />
        <input
          type="date"
          value={jobInfo.date}
          onChange={(e) => setJobInfo({ ...jobInfo, date: e.target.value })}
        />
        <button onClick={generateLog} className="generate-btn">
          Generate AI Daily Log
        </button>
      </div>

      {/* RIGHT SIDE */}
      <div className="notes-section">
        <h2>Office / Insurance Notes</h2>

        <label>Payment Status</label>
        <select
          value={officeInfo.paymentStatus}
          onChange={(e) =>
            setOfficeInfo({ ...officeInfo, paymentStatus: e.target.value })
          }
        >
          <option value="">Select...</option>
          <option value="Paid">Paid</option>
          <option value="Waiting on Insurance">Waiting on Insurance</option>
          <option value="Waiting on Adjuster">Waiting on Adjuster</option>
          <option value="Follow-Up Needed">Follow-Up Needed</option>
        </select>

        <label>Office Notes</label>
        <textarea
          placeholder="Any important office notesâ€¦"
          rows={6}
          value={officeInfo.officeNotes}
          onChange={(e) =>
            setOfficeInfo({ ...officeInfo, officeNotes: e.target.value })
          }
        ></textarea>
      </div>

      {/* AI LOG OUTPUT */}
      {aiOutput && (
        <div className="ai-output">
          <h2>AI Generated Daily Log</h2>
          <pre>{aiOutput}</pre>

          {/* PDF Button */}
          <button className="export-btn" onClick={exportToPDF}>
            Export to PDF
          </button>
        </div>
      )}
    </div>
  );
}

export default MitigationForm;
