import React, { useState } from "react";
import jsPDF from "jspdf";
import "../styles.css";

const MitigationForm = () => {
  const [formData, setFormData] = useState({
    customerName: "",
    jobAddress: "",
    claimNumber: "",
    lossType: "Water Damage",
    startDate: "",
    endDate: "",
    moistureTable: [{ room: "", reading: "" }],
    equipmentUsed: "",
    laborHours: "",
    estimatedCost: "",
    notes: "",
    paymentStatus: "Pending",
    clientSignature: "",
    technicianSignature: ""
  });

  const [aiReport, setAiReport] = useState("");

  // HANDLE INPUT CHANGES
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // TABLE HANDLING
  const handleTableChange = (index, field, value) => {
    const updated = [...formData.moistureTable];
    updated[index][field] = value;
    setFormData({ ...formData, moistureTable: updated });
  };

  const addRow = () => {
    setFormData({
      ...formData,
      moistureTable: [...formData.moistureTable, { room: "", reading: "" }]
    });
  };

  // GENERATE INSURANCE PDF
  const generatePDF = () => {
    const doc = new jsPDF();
    doc.text("ToolbeltAI | Mitigation Insurance Report", 10, 10);
    doc.text(`Customer: ${formData.customerName}`, 10, 20);
    doc.text(`Address: ${formData.jobAddress}`, 10, 30);
    doc.text(`Claim #: ${formData.claimNumber}`, 10, 40);
    doc.text(`Loss Type: ${formData.lossType}`, 10, 50);
    doc.text(`Dates: ${formData.startDate} to ${formData.endDate}`, 10, 60);
    doc.text("Moisture Readings:", 10, 75);
    formData.moistureTable.forEach((row, i) => {
      doc.text(`${row.room}: ${row.reading}`, 10, 85 + i * 10);
    });
    doc.text(`Equipment: ${formData.equipmentUsed}`, 10, 120);
    doc.text(`Labor Hours: ${formData.laborHours}`, 10, 130);
    doc.text(`Estimated Cost: $${formData.estimatedCost}`, 10, 140);
    doc.text(`Notes: ${formData.notes}`, 10, 155);
    doc.text(`Tech Signature: ${formData.technicianSignature}`, 10, 170);
    doc.text(`Client Signature: ${formData.clientSignature}`, 10, 180);
    doc.save("insurance_report.pdf");
  };

  // ðŸ”¥ AI WRITE UP FUNCTION
  const generateAIReport = async () => {
    try {
      const response = await fetch("http://localhost:5001/generate-report", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData)
      });

      const data = await response.json();
      setAiReport(data.report);
      alert("AI Report Generated!");
    } catch (err) {
      console.error(err);
      alert("Error generating AI report.");
    }
  };

  return (
    <div className="mitigation-form-container">
      <div className="card form-section">
        <h2>Insurance Mitigation Form</h2>

        <input name="customerName" placeholder="Customer Name" onChange={handleChange} />
        <input name="jobAddress" placeholder="Job Address" onChange={handleChange} />
        <input name="claimNumber" placeholder="Claim Number" onChange={handleChange} />

        <select name="lossType" onChange={handleChange}>
          <option>Water Damage</option>
          <option>Fire Damage</option>
          <option>Mold</option>
          <option>Storm</option>
        </select>

        <label>Start Date:</label>
        <input name="startDate" type="date" onChange={handleChange} />

        <label>End Date:</label>
        <input name="endDate" type="date" onChange={handleChange} />

        <h3>Moisture Readings</h3>
        {formData.moistureTable.map((row, i) => (
          <div key={i} className="table-row">
            <input placeholder="Room" value={row.room} onChange={(e) => handleTableChange(i, "room", e.target.value)} />
            <input placeholder="Reading %" value={row.reading} onChange={(e) => handleTableChange(i, "reading", e.target.value)} />
          </div>
        ))}
        <button className="add-row-btn" onClick={addRow}>âž• Add Room</button>

        <textarea name="equipmentUsed" placeholder="Equipment Used" onChange={handleChange} />
        <input name="laborHours" placeholder="Total Labor Hours" onChange={handleChange} />
        <input name="estimatedCost" placeholder="Estimated Cost $" onChange={handleChange} />
        <textarea name="notes" placeholder="Job Notes" onChange={handleChange} />
        <input name="technicianSignature" placeholder="Tech Signature" onChange={handleChange} />
        <input name="clientSignature" placeholder="Client Signature" onChange={handleChange} />

        <button className="pdf-btn" onClick={generatePDF}>ðŸ“„ Download Insurance PDF</button>
        <button className="ai-btn" onClick={generateAIReport}>ðŸ§  Generate AI Write-Up</button>
      </div>

      {aiReport && (
        <div className="card ai-output">
          <h3>ðŸ§  AI Insurance Write-Up</h3>
          <p>{aiReport}</p>
        </div>
      )}
    </div>
  );
};

export default MitigationForm;
