import React, { useState } from "react";
import jsPDF from "jspdf";
import "../styles.css";

const MitigationForm = () => {
  const [formData, setFormData] = useState({
    customerName: "",
    jobAddress: "",
    claimNumber: "",
    insuranceCompany: "",
    paymentStatus: "",
    outOfPocket: "",
    waitingOnInsurance: "",
    rooms: [
      { name: "", moisture: "", notes: "", image: null }
    ],
    technicianNotes: "",
  });

  const [aiSummary, setAiSummary] = useState("");

  // HANDLE INPUTS
  const handleChange = (e, index = null) => {
    const { name, value } = e.target;
    if (name.includes("rooms")) {
      const [key, idx] = name.split("-");
      const rooms = [...formData.rooms];
      rooms[idx][key] = value;
      setFormData({ ...formData, rooms });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleImageUpload = (e, index) => {
    const file = e.target.files[0];
    const rooms = [...formData.rooms];
    rooms[index].image = file;
    setFormData({ ...formData, rooms });
  };

  const addRoom = () => {
    setFormData({
      ...formData,
      rooms: [...formData.rooms, { name: "", moisture: "", notes: "", image: null }],
    });
  };

  // AI GENERATION (Fake Example)
  const generateAiSummary = () => {
    const summary = `
### AI Mitigation Summary ###
Customer: **${formData.customerName}**
Claim #: **${formData.claimNumber}**
Insurance: **${formData.insuranceCompany}**
Payment Status: **${formData.paymentStatus}**
Waiting on Insurance: **${formData.waitingOnInsurance}**

ROOM REPORTS:
${formData.rooms
  .map(
    (room, i) => `
Room ${i + 1} (${room.name}):
â€¢ Moisture: ${room.moisture}
â€¢ Notes: ${room.notes}
`
  )
  .join("")}

Technician Notes:  
${formData.technicianNotes}

Next Steps: Continue monitoring moisture levels and adjust equipment.
Prepared by ToolbeltAI â€“ Professional Mitigation Logger
`;
    setAiSummary(summary);
  };

  // PDF EXPORT
  const exportPDF = () => {
    const doc = new jsPDF();
    doc.text("ToolbeltAI Mitigation Report", 10, 10);
    doc.text(`Customer: ${formData.customerName}`, 10, 20);
    doc.text(`Job Address: ${formData.jobAddress}`, 10, 30);
    doc.text(`Claim #: ${formData.claimNumber}`, 10, 40);
    doc.text(`Insurance: ${formData.insuranceCompany}`, 10, 50);
    doc.text(`Payment Status: ${formData.paymentStatus}`, 10, 60);
    doc.text(`Waiting On Insurance: ${formData.waitingOnInsurance}`, 10, 70);

    let y = 80;
    formData.rooms.forEach((room, i) => {
      doc.text(`Room ${i + 1}: ${room.name}`, 10, y);
      y += 10;
      doc.text(`Moisture: ${room.moisture}`, 10, y);
      y += 10;
      doc.text(`Notes: ${room.notes}`, 10, y);
      y += 10;
    });

    doc.text("Technician Notes:", 10, y + 10);
    doc.text(formData.technicianNotes || "", 10, y + 20);

    doc.save("mitigation_report.pdf");
  };

  return (
    <div className="form-container">
      <h2>ðŸ§° ToolbeltAI Mitigation Logger</h2>

      {/* CUSTOMER INFO */}
      <h3>ðŸ‘¤ Customer Info</h3>
      <input name="customerName" placeholder="Customer Name" onChange={handleChange} />
      <input name="jobAddress" placeholder="Job Address" onChange={handleChange} />
      <input name="claimNumber" placeholder="Claim Number" onChange={handleChange} />
      <input name="insuranceCompany" placeholder="Insurance Company" onChange={handleChange} />

      {/* PAYMENT SECTION */}
      <h3>ðŸ’° Payment & Status</h3>
      <select name="paymentStatus" onChange={handleChange}>
        <option value="">Select Payment Status</option>
        <option>Paid in Full</option>
        <option>Customer Owes</option>
        <option>Waiting on Insurance</option>
      </select>

      {/* ROOMS SECTION */}
      <h3>ðŸ“¦ Rooms & Moisture Readings</h3>
      {formData.rooms.map((room, i) => (
        <div key={i} className="room-section">
          <input name="name-0" placeholder="Room Name (e.g., Kitchen)" onChange={(e) => handleChange({ target: { name: `name-${i}`, value: e.target.value }})}/>
          <input name="moisture-0" placeholder="Moisture Readings" onChange={(e) => handleChange({ target: { name: `moisture-${i}`, value: e.target.value }})}/>
          <textarea name="notes-0" placeholder="Room Notes" onChange={(e) => handleChange({ target: { name: `notes-${i}`, value: e.target.value }})}/>
          <input type="file" onChange={(e) => handleImageUpload(e, i)} />
        </div>
      ))}
      <button onClick={addRoom}>âž• Add Another Room</button>

      {/* TECH NOTES */}
      <h3>ðŸ§  Technician Notes</h3>
      <textarea name="technicianNotes" placeholder="Technician Notes" onChange={handleChange} />

      <button onClick={generateAiSummary} className="btn-ai">âš™ Generate AI Summary</button>

      {aiSummary && (
        <div className="ai-box">
          <h3>ðŸ§¾ AI Summary</h3>
          <pre>{aiSummary}</pre>
        </div>
      )}

      <button onClick={exportPDF} className="btn-export">ðŸ“„ Export PDF</button>
    </div>
  );
};

export default MitigationForm;
