import React, { useState } from "react";
import axios from "../api";

export default function MitigationForm() {
  const [customerName, setCustomerName] = useState("");
  const [claimNumber, setClaimNumber] = useState("");
  const [paymentStatus, setPaymentStatus] = useState("Waiting on Insurance");

  const [rooms, setRooms] = useState([
    {
      name: "",
      moisture: "",
      readings: "",
      notes: "",
      photos: [],
    },
  ]);

  const [dryLogs, setDryLogs] = useState([
    { date: "", room: "", moistureLevel: "" },
  ]);

  const [aiOutput, setAiOutput] = useState("");
  const [loadingAI, setLoadingAI] = useState(false);

  const addRoom = () => {
    setRooms([
      ...rooms,
      { name: "", moisture: "", readings: "", notes: "", photos: [] },
    ]);
  };

  const addDryLog = () => {
    setDryLogs([...dryLogs, { date: "", room: "", moistureLevel: "" }]);
  };

  const handlePhotoUpload = (index, files) => {
    const updatedRooms = [...rooms];
    updatedRooms[index].photos = [...files];
    setRooms(updatedRooms);
  };

  const generateAI = async () => {
    setLoadingAI(true);
    try {
      const res = await axios.post("/ai/mitigation-summary", {
        customerName,
        claimNumber,
        paymentStatus,
        rooms,
        dryLogs,
      });
      setAiOutput(res.data.summary);
    } catch (err) {
      console.error(err);
      setAiOutput("ERROR generating summary.");
    }
    setLoadingAI(false);
  };

  return (
    <div style={{ maxWidth: "900px", margin: "auto", padding: "20px" }}>
      <h1>üõ†Ô∏è ToolbeltAI ‚Äì Mitigation Report</h1>

      <div className="section-card">
        <h2>Customer Info</h2>
        <input
          type="text"
          placeholder="Customer Name"
          value={customerName}
          onChange={(e) => setCustomerName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Claim Number"
          value={claimNumber}
          onChange={(e) => setClaimNumber(e.target.value)}
        />

        <select
          value={paymentStatus}
          onChange={(e) => setPaymentStatus(e.target.value)}
          style={{ marginTop: "8px" }}
        >
          <option>Waiting on Insurance</option>
          <option>Insurance Approved</option>
          <option>Paid</option>
        </select>
      </div>

      <div className="section-card">
        <h2>Rooms & Moisture Readings</h2>

        {rooms.map((room, i) => (
          <div key={i} className="room-card">
            <input
              type="text"
              placeholder="Room Name"
              value={room.name}
              onChange={(e) => {
                const r = [...rooms];
                r[i].name = e.target.value;
                setRooms(r);
              }}
            />

            <input
              type="text"
              placeholder="Moisture % (ex: 25% walls)"
              value={room.moisture}
              onChange={(e) => {
                const r = [...rooms];
                r[i].moisture = e.target.value;
                setRooms(r);
              }}
            />

            <textarea
              placeholder="Moisture Readings Details"
              value={room.readings}
              onChange={(e) => {
                const r = [...rooms];
                r[i].readings = e.target.value;
                setRooms(r);
              }}
            />

            <textarea
              placeholder="Room Notes (what was removed/dried)"
              value={room.notes}
              onChange={(e) => {
                const r = [...rooms];
                r[i].notes = e.target.value;
                setRooms(r);
              }}
            />

            <label>Upload Photos:</label>
            <input
              type="file"
              multiple
              onChange={(e) => handlePhotoUpload(i, e.target.files)}
            />
          </div>
        ))}

        <button onClick={addRoom} className="add-btn">
          ‚ûï Add Room
        </button>
      </div>

      <div className="section-card">
        <h2>Daily Dry Log</h2>

        {dryLogs.map((log, i) => (
          <div key={i} className="room-card">
            <input
              type="date"
              value={log.date}
              onChange={(e) => {
                const d = [...dryLogs];
                d[i].date = e.target.value;
                setDryLogs(d);
              }}
            />

            <input
              type="text"
              placeholder="Room"
              value={log.room}
              onChange={(e) => {
                const d = [...dryLogs];
                d[i].room = e.target.value;
                setDryLogs(d);
              }}
            />

            <input
              type="text"
              placeholder="Moisture Level (%)"
              value={log.moistureLevel}
              onChange={(e) => {
                const d = [...dryLogs];
                d[i].moistureLevel = e.target.value;
                setDryLogs(d);
              }}
            />
          </div>
        ))}

        <button onClick={addDryLog} className="add-btn">
          ‚ûï Add Dry Log
        </button>
      </div>

      <button onClick={generateAI} className="generate-btn">
        {loadingAI ? "‚è≥ Generating‚Ä¶" : "‚ö° Generate AI Summary"}
      </button>

      <textarea
        className="output-box"
        value={aiOutput}
        readOnly
        placeholder="AI summary will appear here..."
      ></textarea>
    </div>
  );
}
