import React, { useState } from "react";
import "../styles.css";
import jsPDF from "jspdf";

const MitigationForm = () => {
  const [formData, setFormData] = useState({
    customerName: "",
    jobAddress: "",
    claimNumber: "",
    jobType: "",
    equipmentUsed: "",
    paymentStatus: "",
    waitingInsurance: "",
    technicianNotes: "",
  });

  const [rooms, setRooms] = useState([{ name: "", readings: "", photo: null, aiSummary: "" }]);
  const [aiPsychrometrics, setAiPsychrometrics] = useState("");
  const [aiJobSummary, setAiJobSummary] = useState("");

  const handleFormChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleRoomChange = (index, key, value) => {
    const updatedRooms = [...rooms];
    updatedRooms[index][key] = value;
    setRooms(updatedRooms);
  };

  const addRoom = () => {
    setRooms([...rooms, { name: "", readings: "", photo: null, aiSummary: "" }]);
  };

  const generateAIforRoom = (index) => {
    const example = `
Room: ${rooms[index].name}
Readings: ${rooms[index].readings}
AI Summary:
â€¢ Elevated moisture detected.
â€¢ Recommend demo of damaged materials.
â€¢ Set equipment to maintain drying <40% RH.
    `;
    handleRoomChange(index, "aiSummary", example);
  };

  const generateAIpsychrometrics = () => {
    setAiPsychrometrics(`
Psychrometric Table:
â€¢ DB Temp: 72Â°F
â€¢ RH: 45%
â€¢ Dew Point: 48Â°F
â€¢ GPP: 35
â€¢ Vapor Pressure: 0.42 inHg
ðŸ“Œ Meets IICRC S500 standard.
    `);
  };

  const generateAIJobSummary = () => {
    setAiJobSummary(`
Insurance Summary:
â€¢ Customer: ${formData.customerName}
â€¢ Job Type: ${formData.jobType}
â€¢ Claim #: ${formData.claimNumber}
â€¢ Payment Status: ${formData.paymentStatus}
â€¢ Waiting on Insurance: ${formData.waitingInsurance}
âš  All equipment must remain until moisture is below industry standard (under 16%).
`);
  };

  const generatePDF = () => {
    const doc = new jsPDF({ orientation: "p", unit: "mm", format: "a4" });
    doc.setFontSize(14);
    doc.text("ðŸ›  Mitigation Report - ToolbeltAI", 10, 15);
    doc.setFontSize(11);

    doc.text("ðŸ“ Job Information", 10, 25);
    doc.text(`Customer: ${formData.customerName}`, 10, 32);
    doc.text(`Job Address: ${formData.jobAddress}`, 10, 38);
    doc.text(`Claim #: ${formData.claimNumber}`, 10, 44);
    doc.text(`Job Type: ${formData.jobType}`, 10, 50);

    doc.text("ðŸ’° Payment & Insurance", 10, 60);
    doc.text(`Payment Status: ${formData.paymentStatus}`, 10, 66);
    doc.text(`Waiting on Insurance: ${formData.waitingInsurance}`, 10, 72);

    doc.text("ðŸ“¦ Rooms & Readings", 10, 85);
    rooms.forEach((room, index) => {
      doc.text(`Room ${index + 1}: ${room.name}`, 10, 92 + index * 15);
      doc.text(`Readings: ${room.readings}`, 10, 98 + index * 15);
      if (room.aiSummary) {
        doc.text(`AI Summary: ${room.aiSummary}`, 10, 104 + index * 15);
      }
    });

    if (aiPsychrometrics) {
      doc.text("ðŸŒ¡ Psychrometric Table", 10, 170);
      doc.text(doc.splitTextToSize(aiPsychrometrics, 180), 10, 178);
    }

    if (aiJobSummary) {
      doc.text("ðŸ“Œ FINAL INSURANCE SUMMARY", 10, 210);
      doc.text(doc.splitTextToSize(aiJobSummary, 180), 10, 218);
    }

    doc.save("toolbeltAI_mitigation_report.pdf");
  };

  return (
    <div className="form-container">
      <h2>ðŸ›  ToolbeltAI Mitigation Logger</h2>

      <input name="customerName" placeholder="Customer Name" onChange={handleFormChange} />
      <input name="jobAddress" placeholder="Job Address" onChange={handleFormChange} />
      <input name="claimNumber" placeholder="Claim Number" onChange={handleFormChange} />

      <select name="jobType" onChange={handleFormChange}>
        <option>Select Job Type</option>
        <option>Water Damage</option>
        <option>Fire Damage</option>
        <option>Mold Remediation</option>
        <option>Sewage Backup</option>
        <option>Storm Damage</option>
      </select>

      <select name="paymentStatus" onChange={handleFormChange}>
        <option>Select Payment Status</option>
        <option>Paid in Full</option>
        <option>Waiting on Insurance</option>
        <option>Denied / Collections</option>
      </select>

      <select name="waitingInsurance" onChange={handleFormChange}>
        <option>Waiting on Insurance?</option>
        <option>Yes</option>
        <option>No</option>
      </select>

      <h3>ðŸ“¦ Rooms</h3>
      {rooms.map((room, index) => (
        <div key={index} className="room-card">
          <input placeholder="Room Name" onChange={(e) => handleRoomChange(index, "name", e.target.value)} />
          <textarea placeholder="Moisture Readings" onChange={(e) => handleRoomChange(index, "readings", e.target.value)} />
          <input type="file" onChange={(e) => handleRoomChange(index, "photo", e.target.files[0])} />
          <button onClick={() => generateAIforRoom(index)}>âš™ AI Room Summary</button>

          {room.aiSummary && <div className="ai-box"><p>{room.aiSummary}</p></div>}
        </div>
      ))}
      <button onClick={addRoom}>âž• Add Room</button>

      <h3>ðŸŒ¡ Psychrometric Table</h3>
      <button onClick={generateAIpsychrometrics}>âš™ Generate Psychrometric Table (AI)</button>
      {aiPsychrometrics && <div className="ai-box"><p>{aiPsychrometrics}</p></div>}

      <button onClick={generateAIJobSummary}>ðŸ§  AI Final Job Summary</button>
      {aiJobSummary && <div className="ai-box"><p>{aiJobSummary}</p></div>}

      <button className="pdf-btn" onClick={generatePDF}>
        ðŸ“„ Export Insurance-Level PDF
      </button>
    </div>
  );
};

export default MitigationForm;
